<div id="pdfEmbed"></div>

<script>

    function isMobileDevice() {
        return /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
    }


    function setPdfEmbed() {
        var pdfSrc = '/2023/05/31/資訊研究社-社長/資訊研究社-社長.pdf';
        var zoomLevel = isMobileDevice() ? 10 : 50; 

        var iframeHtml = '<iframe src="' + pdfSrc + '#zoom=' + zoomLevel + '" width="80%" height="600px"></iframe>';
        document.getElementById('pdfEmbed').innerHTML = iframeHtml;
    }


    setPdfEmbed();


    window.addEventListener('resize', setPdfEmbed);
</script>

